func TestInput2EInputForVirtualboxCredentialCreate(t *testing.T) {
	Convey("test input 2 einput for 创建虚拟机的凭证", t, func() {
		input, _ := generate.InputJson2Input([]byte("{\"id\":\"virtualboxv1_1_9/credential/Input\",\"title\":{},\"fold\":false,\"showType\":\"list\",\"fields\":[{\"id\":\"virtualboxv1_1_9/credential/name\",\"title\":{\"text\":\"凭证名称\",\"docLink\":\"BLANK\"},\"edit\":true,\"value\":[\"www\"],\"defaultValue\":[\"\"],\"inputType\":\"input\",\"validate\":{\"@type\":\"type.googleapis.com/ui.ValidateInput\",\"validateDes\":\"2-20个汉字、字母和数字至少包含1种\",\"require\":true,\"requireDes\":\"请输入凭证名称\",\"regex\":\"^[a-zA-Z0-9\\\\\\\\u4e00-\\\\\\\\u9fa5]{2,20}$\",\"regexDes\":\"请输入符合格式的凭证名称\",\"placeHolder\":\"2-20个汉字、字母和数字至少包含1种\"}},{\"id\":\"virtualboxv1_1_9/credential/userName\",\"title\":{\"text\":\"授权账号\",\"docLink\":\"BLANK\"},\"edit\":true,\"value\":[\"root\"],\"defaultValue\":[\"\"],\"inputType\":\"input\",\"validate\":{\"@type\":\"type.googleapis.com/ui.ValidateInput\",\"require\":true}},{\"id\":\"virtualboxv1_1_9/credential/password\",\"title\":{\"text\":\"授权密码\",\"docLink\":\"BLANK\"},\"edit\":true,\"value\":[\"123456\"],\"defaultValue\":[\"\"],\"inputType\":\"password\",\"validate\":{\"@type\":\"type.googleapis.com/ui.ValidateInput\",\"require\":true}}],\"Buttons\":[{\"name\":{},\"method\":\"post\",\"url\":\"/stdb/api/v1.0/credential/create?driverId=virtualboxv1_1_9\",\"body\":{}}]}"))
		eInput := Input2EInput(input)
		ShouldNotBeNil(eInput)
		eInputBytes, _ := json.Marshal(eInput)
		eInputJSON, _ := simplejson.NewJson(eInputBytes)
		credentialInputJSON := eInputJSON.Get("virtualboxv1_1_9/credential/Input")

		Convey("获取凭证名", func() {
			name, _ := credentialInputJSON.Get("virtualboxv1_1_9/credential/name").String()
			ShouldEqual(name, "www")
		})
		Convey("获取用户名", func() {
			userName, _ := credentialInputJSON.Get("virtualboxv1_1_9/credential/userName").String()
			ShouldEqual(userName, "root")
		})
		Convey("获取密码", func() {
			password, _ := credentialInputJSON.Get("virtualboxv1_1_9/credential/password").String()
			ShouldEqual(password, "123456")
		})
	})
}

